import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from "./assets/logo.png";

// Import background images
import signupBg from "./assets/signup-bg.png";
import loginBg from "./assets/login-bg.png";
import dashboardBg from "./assets/dashboard-bg.jpg";

function App() {
  const [token, setToken] = useState(null);
  const [showSignup, setShowSignup] = useState(true);
  const [aiResponse, setAiResponse] = useState("");
  const [prompt, setPrompt] = useState("");
  const [name, setName] = useState("");

  const handleAI = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/ai", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ prompt }),
      });
      const data = await res.json();
      setAiResponse(data.response || "No response");
    } catch (err) {
      console.error(err);
      setAiResponse("Error fetching AI response");
    }
  };

  const handleLogout = () => {
    setToken(null);
    setPrompt("");
    setAiResponse("");
  };

  return (
    <>
      {!token ? (
        showSignup
          ? <Signup setShowSignup={setShowSignup} />
          : <Login setToken={setToken} setName={setName} setShowSignup={setShowSignup} />
      ) : (
        <Dashboard
          name={name}
          prompt={prompt}
          setPrompt={setPrompt}
          aiResponse={aiResponse}
          handleAI={handleAI}
          handleLogout={handleLogout}
        />
      )}
    </>
  );
}

// Signup Component
const Signup = ({ setShowSignup }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameInput, setNameInput] = useState("");

  const handleSignup = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password, name: nameInput }),
      });
      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.detail || "Signup failed");
      }
      alert("Signup successful! Please login.");
      setShowSignup(false);
    } catch (err) {
      console.error(err);
      alert(err.message);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundImage: `url(${signupBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "20px",
      }}
    >
      <div className="container mt-5">
        <div className="card mx-auto shadow-lg" style={{ maxWidth: "400px" }}>
          <div className="card-body">
            <div className="text-center mb-3">
              <img src={logo} alt="Phoenician Capital" style={{ height: "80px" }} />
            </div>
            <h3 className="card-title mb-4 text-center">Signup</h3>
            <input type="text" className="form-control mb-2" placeholder="Name" value={nameInput} onChange={e => setNameInput(e.target.value)} />
            <input type="email" className="form-control mb-2" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
            <input type="password" className="form-control mb-2" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
            <button className="btn btn-primary w-100" onClick={handleSignup}>Signup</button>
            <p className="mt-3 text-center">
              Already have an account? <button className="btn btn-link p-0" onClick={() => setShowSignup(false)}>Login</button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

// Login Component
const Login = ({ setToken, setName, setShowSignup }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const formData = new URLSearchParams();
      formData.append("username", email);
      formData.append("password", password);

      const res = await fetch("http://127.0.0.1:8000/token", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: formData.toString(),
      });
      const data = await res.json();
      setToken(data.access_token);
      setName(email);
    } catch (err) {
      console.error(err);
      alert("Login failed");
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundImage: `url(${loginBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "20px",
      }}
    >
      <div className="container mt-5">
        <div className="card mx-auto shadow-lg" style={{ maxWidth: "400px" }}>
          <div className="card-body">
            <div className="text-center mb-3">
              <img src={logo} alt="Phoenician Capital" style={{ height: "80px" }} />
            </div>
            <h3 className="card-title mb-4 text-center">Login</h3>
            <input type="email" className="form-control mb-2" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
            <input type="password" className="form-control mb-2" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
            <button className="btn btn-success w-100" onClick={handleLogin}>Login</button>
            <p className="mt-3 text-center">
              Don't have an account? <button className="btn btn-link p-0" onClick={() => setShowSignup(true)}>Signup</button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

// Dashboard Component
const Dashboard = ({ name, prompt, setPrompt, aiResponse, handleAI, handleLogout }) => {
  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundImage: `url(${dashboardBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        paddingTop: "20px",
      }}
    >
      <div className="container mt-5">
        <div className="card shadow-lg">
          <div className="card-body">
            <div className="text-center mb-3">
              <img src={logo} alt="Phoenician Capital" style={{ height: "80px" }} />
            </div>
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Welcome, {name}</h2>
              <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
            </div>

            <h4>Ask Gemini AI</h4>
            <textarea
              className="form-control mb-3"
              placeholder="Enter your prompt here..."
              value={prompt}
              onChange={e => setPrompt(e.target.value)}
              rows={4}
            />
            <button className="btn btn-primary mb-3" onClick={handleAI}>Submit Prompt</button>

            <h5>AI Response:</h5>
            <div className="border rounded p-3" style={{ minHeight: "150px", whiteSpace: "pre-wrap" }}>
              {aiResponse}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
