import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import logo from "./assets/logo.png";
function App() {
  const [prompt, setPrompt] = useState("");
  const [output, setOutput] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setOutput("Loading...");

    try {
      const response = await fetch("http://127.0.0.1:8000/ai", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });

      const data = await response.json();

      // ✅ FIX: Ensure we only set text
      const text =
        typeof data.response === "string"
          ? data.response
          : JSON.stringify(data.response);

      setOutput(text);
    } catch (error) {
      console.error("Error:", error);
      setOutput("❌ Error fetching response from AI");
    }
  };

  return (
    <div className="container py-5">
      {/* Navbar */}
      <nav className="navbar navbar-light bg-light mb-4 p-3 rounded shadow-sm">
        <a className="navbar-brand d-flex align-items-center" href="/">
          <img
            src={logo}
            alt="Logo"
            width="50"
            height="50"
            className="d-inline-block align-top mr-2"
          />
          <span className="ml-2">AI Investor Portal</span>
        </a>
      </nav>

      {/* Form */}
      <div className="card shadow-sm p-4">
        <h3 className="mb-3">Ask the AI</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <textarea
              className="form-control mb-3"
              rows="4"
              placeholder="Type your question here..."
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>

      {/* Output */}
      {output && (
        <div className="card mt-4 shadow-sm p-4 bg-light">
          <h4>Response:</h4>
          <pre style={{ whiteSpace: "pre-wrap" }}>{output}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
