import React, { useState } from "react";

function App() {
  const [token, setToken] = useState(null);      // JWT token
  const [showSignup, setShowSignup] = useState(true);
  const [aiResponse, setAiResponse] = useState(""); // Gemini response
  const [prompt, setPrompt] = useState("");       // AI prompt
  const [name, setName] = useState("");           // Logged-in user's name

  // --- Handle AI prompt submission ---
  const handleAI = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/ai", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ prompt }),
      });
      const data = await res.json();
      setAiResponse(data.response);
    } catch (err) {
      console.error(err);
      setAiResponse("Error fetching AI response");
    }
  };

  // --- Signup component ---
  const Signup = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [nameInput, setNameInput] = useState("");

    const handleSignup = async () => {
      try {
        const res = await fetch("http://127.0.0.1:8000/signup", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, password, name: nameInput }),
        });
        const data = await res.json();
        alert("Signup successful! Please login.");
        setShowSignup(false);
      } catch (err) {
        console.error(err);
        alert("Signup failed");
      }
    };

    return (
      <div style={{ padding: "20px" }}>
        <h2>Signup</h2>
        <input placeholder="Name" value={nameInput} onChange={e => setNameInput(e.target.value)} />
        <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        <button onClick={handleSignup}>Signup</button>
        <p>
          Already have an account? <button onClick={() => setShowSignup(false)}>Login</button>
        </p>
      </div>
    );
  };

  // --- Login component ---
  const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = async () => {
      try {
        const formData = new URLSearchParams();
        formData.append("username", email);
        formData.append("password", password);

        const res = await fetch("http://127.0.0.1:8000/token", {
          method: "POST",
          headers: { "Content-Type": "application/x-www-form-urlencoded" },
          body: formData.toString(),
        });

        const data = await res.json();
        setToken(data.access_token);
        setName(email);
      } catch (err) {
        console.error(err);
        alert("Login failed");
      }
    };

    return (
      <div style={{ padding: "20px" }}>
        <h2>Login</h2>
        <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        <button onClick={handleLogin}>Login</button>
        <p>
          Don't have an account? <button onClick={() => setShowSignup(true)}>Signup</button>
        </p>
      </div>
    );
  };

  // --- Dashboard (after login) ---
  const Dashboard = () => {
    return (
      <div style={{ padding: "20px" }}>
        <h2>Welcome, {name}</h2>
        <h3>Ask Gemini AI:</h3>
        <textarea
          placeholder="Enter your prompt here..."
          value={prompt}
          onChange={e => setPrompt(e.target.value)}
          rows={4}
          cols={50}
        />
        <br />
        <button onClick={handleAI} style={{ marginTop: "10px" }}>Submit Prompt</button>
        <h4>AI Response:</h4>
        <div style={{ whiteSpace: "pre-wrap", border: "1px solid #ccc", padding: "10px", marginTop: "10px" }}>
          {aiResponse}
        </div>
      </div>
    );
  };

  return (
    <div>
      {!token ? (
        showSignup ? <Signup /> : <Login />
      ) : (
        <Dashboard />
      )}
    </div>
  );
}

export default App;
