import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import logo from "./assets/logo.png";

function App() {
  const [prompt, setPrompt] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);

  // Registration state
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [registered, setRegistered] = useState(false);

  // Handle Registration
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:8000/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        setRegistered(true);
        alert("✅ Registration successful! You can now use the AI.");
      } else {
        const errorData = await response.json();
        alert("❌ Registration failed: " + errorData.detail);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("❌ Registration error");
    }
  };

  // Handle AI request
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setOutput("Loading...");

    try {
      const response = await fetch("http://127.0.0.1:8000/ai", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });

      const data = await response.json();

      let text = data.response;
      if (typeof text !== "string") {
        text = JSON.stringify(text, null, 2);
      }

      setOutput(text);
    } catch (error) {
      console.error("Error:", error);
      setOutput("❌ Error fetching response from AI");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container-fluid bg-light min-vh-100">
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm px-4">
        <a className="navbar-brand d-flex align-items-center" href="/">
          <img
            src={logo}
            alt="Logo"
            width="45"
            height="45"
            className="d-inline-block align-top mr-2"
          />
          <span className="ml-2 h5 mb-0">AI Investor Portal</span>
        </a>
      </nav>

      {/* Dashboard */}
      <div className="container py-5">
        <div className="row">
          {/* Left column: Registration + AI form */}
          <div className="col-lg-6">
            {/* Registration Card */}
            {!registered && (
              <div className="card shadow-sm p-4 mb-4">
                <h4 className="mb-3">Register</h4>
                <form onSubmit={handleRegister}>
                  <div className="form-group">
                    <input
                      type="text"
                      className="form-control mb-3"
                      placeholder="Username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="password"
                      className="form-control mb-3"
                      placeholder="Password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </div>
                  <button type="submit" className="btn btn-success">
                    Register
                  </button>
                </form>
              </div>
            )}

            {/* AI Prompt Form */}
            {registered && (
              <div className="card shadow-sm p-4 mb-4">
                <h4 className="mb-3">Ask the AI</h4>
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <textarea
                      className="form-control mb-3"
                      rows="6"
                      placeholder="Type your question here..."
                      value={prompt}
                      onChange={(e) => setPrompt(e.target.value)}
                    />
                  </div>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? "Processing..." : "Submit"}
                  </button>
                </form>
              </div>
            )}
          </div>

          {/* Right column: Output */}
          <div className="col-lg-6">
            {output && (
              <div className="card shadow-sm p-4 bg-white">
                <h4>Response:</h4>
                <pre
                  className="border rounded p-3 bg-light text-dark"
                  style={{ whiteSpace: "pre-wrap" }}
                >
                  {output}
                </pre>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
